      "import { CordProvider } from '@cord-sdk/react';\n\n\nimport { useCordSampleToken_DEMO_ONLY_NOT_FOR_PRODUCTION } from './utils';\nimport { VideoPlayer } from './components/VideoPlayer';\n// The demo token is only used on cord.com where there are pre-populated users\n// and messages, you can ignore it!\nimport { prepopulatedDemoToken } from './prepopulatedDemoToken.json';\n\nexport default function App() {\n  const sampleToken = useCordSampleToken_DEMO_ONLY_NOT_FOR_PRODUCTION();\n  const clientAuthToken = prepopulatedDemoToken ?? sampleToken;\n\n  return (\n    // All the Cord React components must be children of a single CordProvider component.\n    <CordProvider\n      clientAuthToken={clientAuthToken}\n      screenshotOptions={{\n        captureWhen: ['new-message', 'new-thread'],\n      }}\n      onInitError={(e) =>\n        console.error('Error when initializing Cord sdk: ', e)\n      }\n    >\n      \n      {clientAuthToken && (\n        <VideoPlayer\n          video={\n            'https://cdn.cord.com/cord-website-video/cord-website-video-with-subs-1080p.mp4'\n          }\n        />\n      )}\n    </CordProvider>\n  );\n}\n",
    'src/ThreadsContext.tsx':
      "import type { PropsWithChildren } from 'react';\nimport {\n  createContext,\n  useCallback,\n  useEffect,\n  useState,\n  useMemo,\n} from 'react';\nimport { thread } from '@cord-sdk/react';\nimport type { Location } from '@cord-sdk/types';\n\nexport type ThreadMetadata = {\n  xPercent: number;\n  yPercent: number;\n  timestamp: number;\n  autogenerated?: boolean;\n  initallyOpen?: boolean;\n  durationOnVideo?: number;\n};\n\n// Context for storing all thread related information\ntype ThreadsContextType = {\n  // Map of all threads on current page, mapping from thread's ID to its\n  // metadata\n  threads: ReadonlyMap<\n    string,\n    { metadata: ThreadMetadata; totalMessages: number }\n  >;\n  // Adds a thread to the threads map\n  addThread: (\n    threadId: string,\n    metadata: ThreadMetadata,\n    totalMessages: number,\n  ) => void;\n  // Removes a thread from the threads map\n  removeThread: (threadId: string) => void;\n\n  // The id of the thread open on this page (or null if none is open)\n  openThread: string | null;\n  setOpenThread: (arg: string | null) => void;\n\n  allowAutofocus: boolean;\n};\nexport const ThreadsContext = createContext<ThreadsContextType | undefined>(\n  undefined,\n);\n\nexport function ThreadsProvider({\n  children,\n  location,\n  delayAutofocus = false,\n}: PropsWithChildren<{ location: Location; delayAutofocus?: boolean }>) {\n  const [threads, setThreads] = useState<\n    Map<string, { metadata: ThreadMetadata; totalMessages: number }>\n  >(new Map());\n  const addThread = useCallback(\n    (threadId: string, metadata: ThreadMetadata, totalMessages: number) =>\n      setThreads((oldThreads) => {\n        if (\n          oldThreads.has(threadId) &&\n          oldThreads.get(threadId)?.totalMessages === totalMessages\n        ) {\n          return oldThreads;\n        }\n        const newThreads = new Map(oldThreads);\n        newThreads.set(threadId, { metadata, totalMessages });\n        return newThreads;\n      }),\n    [],\n  );\n  const removeThread = useCallback(\n    (threadId: string) =>\n      setThreads((oldThreads) => {\n        if (!oldThreads.has(threadId)) {\n          return oldThreads;\n        }\n        const newThreads = new Map(oldThreads);\n        newThreads.delete(threadId);\n        return newThreads;\n      }),\n    [],\n  );\n\n  // Fetch existing threads associated with location\n  const {\n    threads: threadSummaries,\n    hasMore,\n    loading,\n    fetchMore,\n  } = thread.useThreads({ filter: { location, resolvedStatus: 'unresolved' } });\n  useEffect(() => {\n    if (loading) {\n      return;\n    }\n    if (hasMore) {\n      // NOTE: For this demo, fetch all threads on the page.\n      void fetchMore(1000);\n    }\n    threadSummaries\n      .filter((t) => t.total > 0 && Object.keys(t.metadata).length > 0)\n      .forEach((t) => addThread(t.id, t.metadata as ThreadMetadata, t.total));\n  }, [addThread, fetchMore, hasMore, loading, threadSummaries, threads]);\n\n  const [openThread, setOpenThreadReal] = useState<string | null>(null);\n  const [allowAutofocus, setAllowAutofocus] = useState(!delayAutofocus);\n  const setOpenThread = useCallback(\n    (v: string | null) => {\n      if (openThread !== null && v !== openThread) {\n        // Start allowing autofocus (if not already allowed) after we have\n        // opened a (non-null) thread.\n        setAllowAutofocus(true);\n      }\n      setOpenThreadReal(v);\n    },\n    [openThread],\n  );\n\n  const context = useMemo(\n    () => ({\n      threads,\n      addThread,\n      removeThread,\n      openThread,\n      setOpenThread,\n      allowAutofocus,\n    }),\n    [\n      threads,\n      addThread,\n      removeThread,\n      openThread,\n      setOpenThread,\n      allowAutofocus,\n    ],\n  );\n  return (\n    <ThreadsContext.Provider value={context}>\n      {children}\n    </ThreadsContext.Provider>\n  );\n}\n",
